// üõ°Ô∏è Fun√ß√£o para converter JsProxy para Python nativo
function jsproxy_to_python(obj) {
  if (obj === null || obj === undefined) return null;

  if (typeof obj === 'object') {
    if (Array.isArray(obj)) {
      return obj.map(item => jsproxy_to_python(item));
    } else {
      const result = {};
      for (const key in obj) {
        if (obj.hasOwnProperty(key)) {
          result[key] = jsproxy_to_python(obj[key]);
        }
      }
      return result;
    }
  }

  return obj;
}

// üîß Fun√ß√£o para escape SQL
function escape_sql(value) {
  if (value === null || value === undefined || value === '') return 'NULL';
  if (typeof value === 'number') return value;
  if (typeof value === 'boolean') return value;

  // Truncar strings muito longas e escapar aspas
  let str = String(value);
  if (str.length > 255) str = str.substring(0, 255);
  return `'${str.replace(/'/g, "''")}'`;
}

// üìÖ Fun√ß√£o para converter timestamp
function convert_timestamp(timestamp) {
  if (!timestamp) return 'NULL';
  try {
    if (typeof timestamp === 'number') {
      return `'${new Date(timestamp).toISOString()}'`;
    }
    if (typeof timestamp === 'string') {
      return `'${new Date(timestamp).toISOString()}'`;
    }
    return 'NULL';
  } catch (e) {
    return 'NULL';
  }
}

// üßπ Fun√ß√£o para limpar email
function clean_email(email) {
  if (!email || typeof email !== 'string') return null;

  // Remover espa√ßos e converter para min√∫sculo
  email = email.trim().toLowerCase();

  // Valida√ß√£o b√°sica de email
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(email)) return null;

  return email;
}

