// Processar resposta do HubSpot e preparar dados para inserÃ§Ã£o
const contacts = [];
const properties = $('Build Properties List').last().json.properties;

// O HubSpot retorna um objeto com 'total' e 'results'
const hubspotResponse = $input.last().json;
const contactResults = hubspotResponse.results || [];

for (const contact of contactResults) {
  const processedContact = {
    hs_object_id: contact.id,
    lastmodifieddate: contact.properties.lastmodifieddate
  };

  // Mapear todas as propriedades dinamicamente
  for (const prop of properties) {
    const value = contact.properties[prop];

    // Converter tipos de dados conforme necessÃ¡rio
    if (prop === 'createdate' || prop === 'lastmodifieddate' || prop === 'notes_last_contacted') {
      processedContact[prop] = value ? new Date(value).toISOString() : null;
    } else if (prop === 'hubspot_owner_id') {
      processedContact[prop] = value ? parseInt(value) : null;
    } else {
      processedContact[prop] = value || null;
    }
  }

  // Adicionar campos de sistema
  processedContact.created_at = new Date().toISOString();
  processedContact.updated_at = new Date().toISOString();
  processedContact.sync_status = 'active';
  processedContact.hubspot_raw_data = JSON.stringify(contact);

  contacts.push(processedContact);
}

return contacts.map(contact => ({ json: contact }));

// Verificar se todas as colunas necessÃ¡rias existem
const existingColumns = $('Check Table Columns').all().map(item => item.json.column_name);
const requiredColumns = $('Build Properties List').last().json.properties;

const missingColumns = [];
const columnDefinitions = [];

// Buscar definiÃ§Ãµes das colunas faltantes
for (const prop of requiredColumns) {
  if (!existingColumns.includes(prop)) {
    missingColumns.push(prop);

    // Buscar definiÃ§Ã£o da coluna na tabela hubspot_properties
    const propertyDef = $('Get HubSpot Properties').all().find(item => item.json.hs_internal_name === prop);

    if (propertyDef) {
      const dataType = propertyDef.json.data_type;
      let sqlType = 'VARCHAR(500)';

      // Mapear tipos de dados
      switch (dataType) {
        case 'ts':
          sqlType = 'TIMESTAMP';
          break;
        case 'num':
          sqlType = 'INTEGER';
          break;
        case 'bool':
          sqlType = 'BOOLEAN';
          break;
        case 'text':
        default:
          sqlType = 'VARCHAR(500)';
          break;
      }

      columnDefinitions.push({
        column_name: prop,
        sql_type: sqlType,
        data_type: dataType
      });
    }
  }
}

return {
  missing_columns: missingColumns,
  column_definitions: columnDefinitions,
  has_missing_columns: missingColumns.length > 0,
  total_missing: missingColumns.length
};

// Gerar comandos SQL para criar colunas faltantes
const columnDefinitions = $json.column_definitions;
const sqlCommands = [];

for (const col of columnDefinitions) {
  const sql = `ALTER TABLE public.contacts ADD COLUMN IF NOT EXISTS ${col.column_name} ${col.sql_type};`;
  sqlCommands.push(sql);
}

return sqlCommands.map(sql => ({ json: { sql_command: sql } }));

// Preparar dados para upsert em lote usando propriedades do banco
const contacts = $('Aggregate').last().json.data;

// Buscar propriedades do nÃ³ "Build Properties List" (que vem do banco)
const propertiesData = $('Build Properties List').last().json;
const properties = propertiesData.propertiesArray;

// Remover hs_object_id do array properties se existir (evitar duplicata)
const cleanProperties = properties.filter(prop => prop !== 'hs_object_id');

// Construir query de upsert em lote
const columns = ['hs_object_id', ...cleanProperties, 'created_at', 'updated_at', 'sync_status', 'hubspot_raw_data'];

// Construir VALUES para mÃºltiplos registros
const valuesClauses = [];

for (const contact of contacts) {
  const contactData = contact;
  const contactValues = [];

  for (const col of columns) {
    const value = contactData[col];
    contactValues.push(value !== undefined ? value : null);
  }

  // Criar valores diretos com tratamento correto de tipos
  const valuesString = contactValues.map((val, index) => {
    if (val === null) return 'NULL';

    // hs_object_id deve ser integer
    if (columns[index] === 'hs_object_id') {
      return parseInt(val) || 0;
    }

    // Strings com escape correto
    if (typeof val === 'string') {
      return `'${val.replace(/'/g, "''")}'`;
    }

    // Datas
    if (val instanceof Date) {
      return `'${val.toISOString()}'`;
    }

    // NÃºmeros
    if (typeof val === 'number') {
      return val;
    }

    return val;
  }).join(', ');

  valuesClauses.push(`(${valuesString})`);
}

// Construir query final
const updateSet = cleanProperties.map(prop => `${prop} = EXCLUDED.${prop}`).join(', ');
const updateSetFull = `${updateSet}, updated_at = EXCLUDED.updated_at, sync_status = EXCLUDED.sync_status, hubspot_raw_data = EXCLUDED.hubspot_raw_data`;

const upsertQuery = `
  INSERT INTO public.contacts (${columns.join(', ')})
  VALUES ${valuesClauses.join(', ')}
  ON CONFLICT (hs_object_id)
  DO UPDATE SET ${updateSetFull}
`;

return [{
  json: {
    query: upsertQuery,
    totalContacts: contacts.length,
    propertiesUsed: cleanProperties,
    totalProperties: cleanProperties.length
  }
}];

